  saveCapacidadeLinha() {
    if (!this.validateSaveCapacidadeLinha()) {
      this.itensCadCapacidadeLinha.forEach((element: any) => {
        let timeConvert = moment(element.time, "HH:mm:ss");

        let newTime = timeConvert.toDate();

        element.time = newTime.toLocaleTimeString().toString();
      });

      let capacidadeLinha: any = {
