    this.tipo = "";
    this.titulo = "";
    this.placeholderProcesso = "";
    this.codigoProcesso = "";
    this.descricaoProcesso = "";
  }

  formatarSegundos(h: any, min: any, s: any) {
    return formatSegundos(h, min, s);
  }

  dataFormatarSoma(s: any) {
    return dataFormatSoma(s);
  }

  somaQtdeOperadoresCapacidadeProdutiva() {
    let somaLinhaQtdeOepradores = 0;
    let somaLinhaTime = 0;

    //CAPACIDADE LINHA
    this.itensCapacidadeLinha.forEach((elementCapLinha: any) => {
      //HABILIDADE CAPACIDADE LINHA
      elementCapLinha.linhaCapacidadeHabilidades.forEach(
        (elementCapLinhaHabilidade: any) => {
          //SOMA QTDE OPERADORES
          if (elementCapLinhaHabilidade.qtdeOperadores != "") {
            somaLinhaQtdeOepradores += parseFloat(
              elementCapLinhaHabilidade.qtdeOperadores
            );
          }
          this.linha.qtdeOperador = somaLinhaQtdeOepradores;

          if (elementCapLinhaHabilidade.time != "") {
            //CONVERTENDO
            let newTimeConvert = moment(
              elementCapLinhaHabilidade.time,
              "HH:mm:ss"
            );

            let newTime = newTimeConvert.toDate();

            somaLinhaTime += this.formatarSegundos(
              newTime.getHours(),
              newTime.getMinutes(),
              newTime.getSeconds()
            );
          }

          this.linha.capacidadeProdutiva = this.dataFormatarSoma(somaLinhaTime);
        }
      );
    });

    //console.log(this.linha.capacidadeProdutiva)
    this.linha.qtdeOperador = somaLinhaQtdeOepradores;
    this.linha.capacidadeProdutiva = this.dataFormatarSoma(somaLinhaTime);
  }

  carregaCapacidadeLinha(itemsCapacidadeLinha: any): void {
    this.itensCapacidadeLinha = itemsCapacidadeLinha;

    //let somaLinhaQtdeOepradores = 0;
    //CAPACIDADE LINHA
    this.itensCapacidadeLinha.forEach((elementCapLinha: any) => {
      //HABILIDADE CAPACIDADE LINHA
      elementCapLinha.linhaCapacidadeHabilidades.forEach(
        (elementCapLinhaHabilidade: any) => {
          elementCapLinhaHabilidade.time = new Date(
            elementCapLinhaHabilidade.time
          )
            .toLocaleTimeString()
            .toString();
        }
      );
    });

    this.somaQtdeOperadoresCapacidadeProdutiva();
  }

  carregaRecursoTecnicoLinha(itemsRecursoTecnicoLinha: any): void {
    this.itensRecursoTecnicoLinha = itemsRecursoTecnicoLinha;

    //let somaLinhaQtdeOepradores = 0;
    //CAPACIDADE RECURSO TRECNICO LINHA
    this.itensRecursoTecnicoLinha.forEach((elementRecursoTecnicoLinha: any) => {
      //HABILIDADE CAPACIDADE RECURSO TECNICO LINHA
      elementRecursoTecnicoLinha.linhaRecursoTecnicoHabilidades.forEach(
        (elementRecursoTecnicoLinhaHabilidade: any) => {
          elementRecursoTecnicoLinhaHabilidade.time = new Date(
