  async consultaProdutoCadastrado(retorno: any) {
    this.retorno = retorno.sucesso;

    if (this.retorno == false) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Modelo (Produto) j cadastrado.";
      this.snackbar = true;
      return;
    }

    this.linha.idProdutoLocal = this.produto.id;
    this._controller!.update(this.idLinha, this.linha);
  }

  async updateLinha() {
    if (this.validate()) {
    } else {
      //this.solicitacao.user = parseInt(Storage.get("User")!);

      if (this.tipoCad == 1) {
        this._controller!.consultarProdutoCadastrado(this.produto.id);
      }
    }
  }

  carregarDtos(): void {
    this.idLinha = this.$route.params.id;

    if (this.idLinha == undefined) {
      this._controller!.salvar(this.linha);
      this.tipoCad = 1;
    }

    if (this.$route.params.id !== undefined) {
      this.tipoCad = 2;
    }

    //console.log(this.tipoCad);
  }

  showLinhaForEditar(item: any) {
    //CARREGANDO DADOS DO FUNCIONARIO
    //this.linha = item;
    this.idLinha = item.id;
    this.linha.nome = item.nome;
    this.produto = {
      id: item.idProdutoLocal,
      dadosProduto: item.dadosProduto,
    };

    if (item.idProdutoLocal != null && item.dadosProduto != null) {
      this.disabledProduto = true;
    } else {
      this.disabledProduto = false;
    }
  }

  created() {
    this._controller = new LinhaFormController(this);
    this._controller.carregarDtos(this.$route.params.id);
    this.carregarDtos();
    this.isMobile = Display.isMobile();
